import React, {Component, Fragment} from 'react'; 
import Navbar from './Navbar'; 
import OptionsPanel from './OptionsPanel'; 
import ChatBoxScreen from './Screens/ChatBoxScreen'; 
import CreateRoomScreen from './Screens/CreateRoomScreen';
import FloorScreen from './Screens/FloorScreen'; 
import HomeScreen from './Screens/HomeScreen'; 
import ProfileScreen from './Screens/ProfileScreen'; 
import SettingsScreen from './Screens/SettingsScreen';
import SupportScreen from './Screens/SupportScreen';

export default class App extends Component {
  state = {
    screenToLoad: <HomeScreen />,
    chosenFloor: null
  }

  handleSelection = selection => {
    let chosenFloor = this.state.chosenFloor; 
    chosenFloor = selection;
    this.setState({chosenFloor});
  }

  componentDidMount() {
    let screenToLoad;
    const split = window.location.pathname.split('/'); 
    switch(split[1]) {
      case 'home':
        screenToLoad = <HomeScreen />;
        break;
      case 'profile':
        screenToLoad = <ProfileScreen />;
        break;
      case 'settings':
        screenToLoad = <SettingsScreen />;
        break;
      case 'support':
        screenToLoad = <SupportScreen />;
        break;
      case 'f':
        const floor = split[2].charAt(0).toUpperCase() + split[2].slice(1);
        screenToLoad = <FloorScreen floor = {floor} />
        break;
      default:
        screenToLoad = <HomeScreen />;
        break;
    }
    this.setState({screenToLoad});
  }

  render() {
    const divStyle = {
      width: '100%',
      height: 'calc(100% - 60px)',
      display: 'inline-block',
      paddingTop: '60px'
    };

    return (
      <Fragment>
        <Navbar />
        <div style = {divStyle}>
          <OptionsPanel onSelect = {this.props.handleSelection} />
          {this.state.screenToLoad}
        </div>
      </Fragment>
    );
  }
}
